import React, {useState} from 'react'
import { Navigate } from "react-router-dom";
import Navtop from '../../components/common/Navtop';
import insertBookApi from '../../actions/BookAction';
import { ToastContainer, toast } from 'react-toastify';
import "react-toastify/dist/ReactToastify.css";

const BookEntry = () => {
  const [titleErr, setTitleErr] = useState(false);
  const [authorErr, setAuthorErr] = useState(false);
  const [pubErr, setPubErr] = useState(false);
  const [priceErr, setPriceErr] = useState(false);
  const [isbnErr, setIsbnErr] = useState(false);
  const [fileErr, setFileErr] = useState(false);
  const [data, setData] = useState({
    title : "",
    author: "",
    publication: "",
    price:"",
    isbn:"",
    file: ""
  });

  const handleChange = (event) =>{
    setData({ ...data,[event.target.name]: event.target.value});
    console.log(data);
  }

  const validateFormData = () => {
    let isTrue = false;
    if(data.title.length === 0){
      setTitleErr(true);
      isTrue = true;
    }else{
      setTitleErr(false);
    }
    if(data.author.length === 0){
      setAuthorErr(true);
      isTrue = true;
    }else{
      setAuthorErr(false);
    }
    if(data.publication.length === 0){
      setPubErr(true);
      isTrue = true;
    }else{
      setPubErr(false);
    }
    if(data.price.length === 0){
      setPriceErr(true);
      isTrue = true;
    }else{
      setPriceErr(false);
    }
    if(data.isbn.length === 0){
      setIsbnErr(true);
      isTrue = true;
    }else{
      setIsbnErr(false);
    }
    if(data.file.length === 0){
      setFileErr(true);
      isTrue = true;
    }else{
      setFileErr(false);
    }
    return isTrue;
  }
  const submitForm = (event) => {
    event.preventDefault();
    const validFrmData = validateFormData();
    if(validFrmData === false){
      toast.error("All Fields Are Required",{position: toast.POSITION.TOP_CENTER});
    }else{
        const formdata = {
          title : data.title,
          author : data.author,
          pub : data.publication,
          price : data.price,
          isbn : data.isbn,
          file : data.file,
        };
        insertBookApi(formdata).then(
          (res) =>{
            if(res.success === true){
              toast.success(res.status,{position: toast.POSITION.TOP_CENTER});
            }else{
              toast.error(res.status,{position: toast.POSITION.TOP_CENTER});
            }
          },
          (err) =>{
              toast.error(err.message, {
                position: toast.POSITION.TOP_CENTER
            });
          }
        );
      }
  }
  const loggedInUser = localStorage.getItem("authenticated");
  if(!loggedInUser){
    return <Navigate replace to="/" />;
  }else{
    return (
      <div>
        <Navtop/>
          <div class="main-container sidebar-closed sbar-open" id="container">
            <div class="overlay"></div>
            <div class="cs-overlay"></div>
            <div class="search-overlay"></div>
            <div id="content" className="main-content">
            <ToastContainer />
              <div className="container-fluid">
                <div className="page-header">
                    <div className="page-title">
                        Book Hut - New Book Entry
                    </div>
                </div>
                <div className="row justify-content-center">
                  <div className="col-sm-4">
                    <div className="card">
                      <div className="card-header text-center bg-warning">
                          New Book Entry Form
                      </div>
                      <div className="card-body">
                        <form onSubmit={submitForm} >
                          <div className="mb-2">
                            <input 
                              type="text" 
                              className="form-control" 
                              onChange={handleChange} 
                              id="title" 
                              value={data.title}
                              name="title"
                              aria-describedby="titleHelp" 
                              placeholder="Enter Title" />
                              {titleErr ? <span style={{ color: 'red'}}>Title is required</span> : ""}
                          </div>

                          <div className="mb-2">
                            <input type="text" 
                              className="form-control"
                              onChange={handleChange} 
                              id="author"  
                              name="author"
                              value={data.author}
                              placeholder='Enter Author Name'/>
                              {authorErr ? <span style={{ color: 'red'}}>Author Name is required</span> : ""}
                          </div>
                          <div className="mb-2">
                            <input type="text" 
                            className="form-control" 
                            onChange={handleChange}
                            name="publication"
                            value={data.publication} 
                            id="publication" 
                            placeholder='Enter Publication Name'/>
                            {pubErr ? <span style={{ color: 'red'}}>Publication is required</span> : ""}
                          </div>
                          <div className="mb-2">
                            <input type="text" 
                            className="form-control" 
                            onChange={handleChange} 
                            id="price" 
                            name="price"
                            value={data.price}
                            placeholder='Enter Price'/>
                            {priceErr ? <span style={{ color: 'red'}}>Price is required</span> : ""}
                          </div>
                          <div className="mb-2">
                            <input type="text" 
                            className="form-control" 
                            onChange={handleChange} 
                            id="isbn" 
                            name="isbn"
                            value={data.isbn}
                            placeholder='Enter ISBN Number'/>
                            {isbnErr ? <span style={{ color: 'red'}}>ISBN is required</span> : ""}
                          </div>
                          <div class="mb-3">
                            <input className="form-control"
                             onChange={handleChange} 
                             type="file" 
                             name="file"
                             value={data.file}
                             id="formFile" />
                             {fileErr ? <span style={{ color: 'red'}}>File is required</span> : ""}
                          </div>
                          <button type="submit" className="btn btn-primary">Submit</button>
                        </form>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div> 
          </div>
        </div>
      )
    }
}
export default BookEntry;
